@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  standalone: true,
  imports: [
    AddTodoComponent,
    TodoListComponent,
    TodoCounterComponent,
    TodoControlsComponent,
  ],
})
export class AppComponent {}

